INSERT INTO `sd_cdata_correct` VALUES ('ADMISSION', 'HOSPCODE|PID|AN|DATETIME_ADMIT', 'CREATE TABLE IF NOT EXISTS `admission_correct` (\r\n  `ID` bigint(20) NOT NULL AUTO_INCREMENT,\r\n  `EDITOR_CID` varchar(13) NOT NULL,\r\n  `D_MODIFIED` datetime NOT NULL,\r\n  `HOSPCODE` varchar(5) NOT NULL,\r\n  `PID` varchar(15) NOT NULL,\r\n  `SEQ` varchar(16) NOT NULL,\r\n  `AN` varchar(9) NOT NULL,\r\n  `DATETIME_ADMIT` datetime NOT NULL,\r\n  `WARDADMIT` varchar(5) NOT NULL,\r\n  `INSTYPE` varchar(4) NOT NULL,\r\n  `TYPEIN` varchar(1) NOT NULL,\r\n  `REFERINHOSP` varchar(5) DEFAULT NULL,\r\n  `CAUSEIN` varchar(1) DEFAULT NULL,\r\n  `ADMITWEIGHT` decimal(5,1) DEFAULT NULL,\r\n  `ADMITHEIGHT` int(3) DEFAULT NULL,\r\n  `DATETIME_DISCH` datetime NOT NULL,\r\n  `WARDDISCH` varchar(5) NOT NULL,\r\n  `DISCHSTATUS` varchar(1) NOT NULL,\r\n  `DISCHTYPE` varchar(1) NOT NULL,\r\n  `REFEROUTHOSP` varchar(5) DEFAULT NULL,\r\n  `CAUSEOUT` varchar(1) DEFAULT NULL,\r\n  `COST` decimal(11,2) DEFAULT NULL,\r\n  `PRICE` decimal(11,2) NOT NULL,\r\n  `PAYPRICE` decimal(11,2) NOT NULL,\r\n  `ACTUALPAY` decimal(11,2) NOT NULL,\r\n  `PROVIDER` varchar(15) DEFAULT NULL,\r\n  `D_UPDATE` datetime NOT NULL,\r\n  `DRG` varchar(5) DEFAULT \'0\' COMMENT \'กลุ่ม DRG ที่ได้จากการคำนวณข้อมูลผู้ป่วยใน\',\r\n  `RW` decimal(11,4) DEFAULT \'0.0000\' COMMENT \'ค่าน้ำหนักสัมพัทธ์ของผู้ป่วยใน\',\r\n  `ADJRW` decimal(11,4) DEFAULT \'0.0000\' COMMENT \'ค่าน้ำหนักสัมพัทธ์ที่ปรับค่าแล้วของผู้ป่วยใน\',\r\n  `ERROR` varchar(2) DEFAULT \'0\' COMMENT \'รหัสความผิดพลาดเกี่ยวกับข้อมูลผู้ป่วยใน\',\r\n  `WARNING` varchar(4) DEFAULT \'0\',\r\n  `ACTLOS` int(4) DEFAULT \'0\' COMMENT \'ค่าวันนอนจริงของผู้ป่วยใน\',\r\n  `GROUPER_VERSION` varchar(20) DEFAULT \'0\' COMMENT \'เลข Version ของโปรแกรม Grouper\',\r\n  PRIMARY KEY (`ID`)\r\n) ENGINE=InnoDB DEFAULT CHARSET=utf8');
